INSERT INTO `judge_question` VALUES (10031, '五年级奥数模拟测试', '两个加数都是30，和是60。（）', 'T', '两个加数都是30，和是60。即30+30=60。', 5, '4', '第二章', '判断题');
INSERT INTO `judge_question` VALUES (10032, '五年级奥数模拟测试', '某列车通过250米长的隧道用25秒，通过210米长的隧道用23秒，若该列车与另一列长150米。时速为72千米的列车相遇，错车而过需要10秒钟?', 'T', '某列车通过250米长的隧道用25秒，通过210米长的隧道用23秒，若该列车与另一列长150米。时速为72千米的列车相遇，错车而过需要10秒钟', 4, '4', '第二章', '判断题');
INSERT INTO `judge_question` VALUES (10033, '五年级奥数模拟测试', '甲、乙二人加工一批帽子，甲每天比乙多加工10个。途中乙因事休息了5天，20天后，甲加工的帽子正好是乙的2倍，这是两人各加工21个', 'F', '甲、乙二人加工一批帽子，甲每天比乙多加工10个。途中乙因事休息了5天，20天后，甲加工的帽子正好是乙的2倍，这是两人各加工20个', 5, '3', '第二章', '判断题');
