INSERT INTO `multi_question` VALUES (10037, '小学数学二年级', '15里面有（ ）个十。', '1', '2', '3', '4', 'A', '15里面有（1）个十。', 2, '第一章', '2', '选择题');
INSERT INTO `multi_question` VALUES (10052, '小学数学一年级测试', '请你估计一下，（）那个最接近你的年龄。', '10', '20', '30', '200', 'A', '请你估计一下，10那个最接近你的年龄。', 10, '第一章', '3', '选择题');
INSERT INTO `multi_question` VALUES (10053, '小学数学一年级测试', '验算473 - 319= 154，下列验算方法错误的是()。\n', '473-154', '319+154', '473-319', '319-154', 'D', '319-154的答案是错误的，473-319的答案应该是154', 5, '第一章', '4', '选择题');
INSERT INTO `multi_question` VALUES (10054, '小学数学一年级测试', '妈妈买来35颗糖，分给小明9颗，还剩几颗?\n', '35+9', '35-9', '35-6', '35+6', 'B', '妈妈买来35颗糖，分给小明9颗，还剩35-9颗。', 5, '第三章', '5', '选择题');
INSERT INTO `multi_question` VALUES (10055, '小学数学一年级测试', '将一个长方形拉成一个平行四边形，它的周长()\n', '变大', '变小', '不变', '以上都不对', 'C', '将一个长方形拉成一个平行四边形，它的周长不变。', 2, '第一章', '5', '选择题');
INSERT INTO `multi_question` VALUES (10056, '五年级奥数模拟测试', '哥哥和弟弟两人3年后年龄和是27岁，弟弟今年的年龄正好是哥哥和弟弟两人年龄的差。哥哥和弟弟今年各多少岁?', '7', '10', '6', '5', 'A', '解题思路:从题中“哥哥和弟弟两人3年后年龄和是27岁”这句话，可以求出哥哥和弟弟今年的年龄和是27-3×2=21(岁），从“弟弟今年的年龄正好是哥哥和弟弟两人的年龄差”，即哥哥年龄-弟弟年龄=弟弟年龄。可以知道哥哥今年的年龄是弟弟年龄的2倍，弟弟年龄是哥哥年龄的1/2。', 2, '第三章', '3', '选择题');
INSERT INTO `multi_question` VALUES (10057, '五年级奥数模拟测试', '-条路长100米，从头到尾每隔10米栽1棵梧桐树，共栽多少棵树?', '11', '12', '13', '14', 'A', '路分成100÷10=10段，共栽树10+1 =11棵。', 2, '第三章', '4', '选择题');
INSERT INTO `multi_question` VALUES (10058, '五年级奥数模拟测试', '棵柳树排成—排，在每两棵柳树中间种3棵桃树，共种多少棵桃树?', '31', '32', '33', '24', 'C', '3×(12-1) =33棵。', 2, '第三章', '4', '选择题');
INSERT INTO `multi_question` VALUES (10059, '五年级奥数模拟测试', '—根200厘米长的木条，要锯成10厘米长的小段，需要锯几次?', '18', '17', '19', '20', 'C', '200÷10=20段，20-1 =19次。', 2, '第三章', '3', '选择题');
INSERT INTO `multi_question` VALUES (10060, '五年级奥数模拟测试', '蚂蚁爬树枝，每上一节需要10秒钟，从第一节爬到第13节需要多少分钟?', '2', '3', '4', '5', 'A', '从第一节到第13节需10×(13-1) =120秒，120÷60=2分。', 2, '第三章', '3', '选择题');
INSERT INTO `multi_question` VALUES (10061, '五年级奥数模拟测试', '在花圃的周围方式菊花，每隔1米放1盆花。花圃周围共20米长。需放多少盆菊花?', '18', '17', '16', '20', 'D', '20÷1×1 =20盆', 2, '第一章', '3', '选择题');
INSERT INTO `multi_question` VALUES (10062, '五年级奥数模拟测试', '一列火车从甲地开往乙地，开出2。5小时，行了150千米。照这样的速度，再行驶3小时到达乙地。甲、乙两地相距多少千米?', '330', '331', '332', '333', 'A', '先求火车每小时行多少千米，再求共行了几小时，最后求出共行了多少千米(即甲、乙两地距离)。火车每小时行多少千米:150÷2.5=60 (千米)\n火车共行了多少小时:2.5+3=5.5 (小时)甲乙两地相距多少千米:60×5.5=330(千米)\n综合算式:150÷2.5×(2.5+3) =150÷2.5×5.5=60×5.5=330(千米)', 2, '第一章', '5', '选择题');
INSERT INTO `multi_question` VALUES (10063, '五年级奥数模拟测试', '甲、乙两车同时从A、B两地相对开出，甲车每小时比乙车多行20千米。途中乙因修车用了2小时，6小时后甲车到达两地中点，而乙车才行了甲车所行路程的一半。问：A、B两地相距多少米？', '959', '960', '961', '962', 'B', '设乙每小时走X千米 则甲每小时走(X+20)千米 则有 \n6X+120=2*4X  X=60 \n所以甲每小时走80千米  两地相距80*6*2=960千米 ', 2, '第一章', '4', '选择题');
